mapper.select:
select a.id as tid,a.number as tNumber,b.* from g_terminal a left join g_client b on b.id=a.clientId


	
		
			
				and a.addTime >= #{value}
			
			
				
					
						and a.addTime <= #{value}
					
					
						and a.${key} = #{value}
					
				
			
		
	


order by a.id desc
